var thumbnail_mode = "yes"; //yes-dengan gambar,no-tanpa gambar
summary_noimg = 10; 
summary_img = 14; 
img_thumb_height = 100;
img_thumb_width = 100;

//
function removeHtmlTag(strx,chop){ 
    if(strx.indexOf("<")!=-1)
    {
        var s = strx.split("<"); 
        for(var i=0;i<s.length;i++){ 
            if(s[i].indexOf(">")!=-1){ 
                s[i] = s[i].substring(s[i].indexOf(">")+1,s[i].length); 
            } 
        } 
        strx =  s.join(""); 
    }
    chop = (chop < strx.length-1) ? chop : strx.length-2; 
    while(strx.charAt(chop-1)!=' ' && strx.indexOf(' ',chop)!=-1) chop++; 
    strx = strx.substring(0,chop-1); 
    return strx+''; 
}

function createSummaryAndThumb(pID){
    var div = document.getElementById(pID);
    var imgtag = "";
    var img = div.getElementsByTagName("img");
    var summ = summary_noimg;
        if(thumbnail_mode == "yes") {
    if(img.length>=1) {    
       imgtag = '<div class="img_post"><a href="'+img[0].src+'"><img src="'+img[0].src+'" width="162px" height="230px" style=" float:left;margin:-21px 0 0 -3px;"></a></div>';
        summ = summary_img;
    }
    }
  var summary = imgtag + '<div class="keterangan">' + removeHtmlTag(div.innerHTML,summ) + '</div>';
    div.innerHTML = summary;
}
